while IFS= read ecache; do
        echo "Setting Elastic Cache Checks for  :"${ecache};
        aws cloudwatch put-metric-alarm --alarm-name ${ecache}-ECache-CPUUtilization --alarm-description "${ecache} CPU Utilization" --metric-name CPUUtilization --namespace AWS/ElastiCache --statistic Average --period 60 --threshold 75 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name=CacheClusterId,Value=${ecache}" --evaluation-periods 5 --alarm-actions arn:aws:sns:ap-south-1:772389675430:ADDA247-ORANGE-ALERT --profile=prod;
        aws cloudwatch put-metric-alarm --alarm-name ${ecache}-ECache-CPUUtilization --alarm-description "${ecache} CPU Utilization" --metric-name CPUUtilization --namespace AWS/ElastiCache --statistic Average --period 60 --threshold 95 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name=CacheClusterId,Value=${ecache}" --evaluation-periods 5 --alarm-actions arn:aws:sns:ap-south-1:772389675430:ADDA247-RED-ALERT --profile=prod;
        aws cloudwatch put-metric-alarm --alarm-name ${ecache}-ECache-DbMemUsagePerc --alarm-description "${ecache} Db Mem Usage Percentage" --metric-name DatabaseMemoryUsagePercentage --namespace AWS/ElastiCache --statistic Average --period 60 --threshold 80 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name=CacheClusterId,Value=${ecache}" --evaluation-periods 5 --alarm-actions arn:aws:sns:ap-south-1:772389675430:ADDA247-ORANGE-ALERT --profile=prod;
        aws cloudwatch put-metric-alarm --alarm-name ${ecache}-ECache-DbMemUsagePerc --alarm-description "${ecache} Db Mem Usage Percentage" --metric-name DatabaseMemoryUsagePercentage --namespace AWS/ElastiCache --statistic Average --period 60 --threshold 90 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name=CacheClusterId,Value=${ecache}" --evaluation-periods 5 --alarm-actions arn:aws:sns:ap-south-1:772389675430:ADDA247-RED-ALERT --profile=prod;
done < "./elasticcache-prod.txt"
