while IFS= read rdsdb; do
        echo "Setting Database Checks for  :"${rdsdb};
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdb}-RDS-CPUUtilization --alarm-description "${rdsdb} CPU Utilization" --metric-name CPUUtilization --namespace AWS/RDS --statistic Average --period 60 --threshold 90 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBInstanceIdentifier,Value=${rdsdb}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-RED-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdb}-RDS-DatabaseConnections --alarm-description "${rdsdb} Database Connections" --metric-name DatabaseConnections --namespace AWS/RDS --statistic Average --period 60 --threshold 800 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBInstanceIdentifier,Value=${rdsdb}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-RED-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdb}-RDS-Free-Storage --alarm-description "${rdsdb} Free Storage" --metric-name FreeStorageSpace --namespace AWS/RDS --statistic Average --period 300 --threshold 10737418240 --comparison-operator LessThanThreshold  --dimensions "Name= DBInstanceIdentifier,Value=${rdsdb}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-ORANGE-ALERT;
done < "./rdsdb.txt"
