while IFS= read rdsdbcluster; do
        echo "Setting Database Checks for  :"${rdsdbcluster};
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdbcluster}-RDS-CPUUtilization --alarm-description "${rdsdbcluster} CPU Utilization" --metric-name CPUUtilization --namespace AWS/RDS --statistic Average --period 60 --threshold 90 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBClusterIdentifier,Value=${rdsdbcluster}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-RED-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdbcluster}-RDS-BlockedTransactions --alarm-description "${rdsdbcluster} Blocked Transactions" --metric-name BlockedTransactions --namespace AWS/RDS --statistic Average --period 60 --threshold 50 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBClusterIdentifier,Value=${rdsdbcluster}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-RED-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdbcluster}-RDS-DatabaseConnections --alarm-description "${rdsdbcluster} Database Connections" --metric-name DatabaseConnections --namespace AWS/RDS --statistic Average --period 60 --threshold 800 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBClusterIdentifier,Value=${rdsdbcluster}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-RED-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdbcluster}-RDS-DatabaseConnections --alarm-description "${rdsdbcluster} Database Connections" --metric-name DatabaseConnections --namespace AWS/RDS --statistic Average --period 60 --threshold 500 --comparison-operator GreaterThanOrEqualToThreshold  --dimensions "Name= DBClusterIdentifier,Value=${rdsdbcluster}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-ORANGE-ALERT;
        aws cloudwatch put-metric-alarm --alarm-name ${rdsdbcluster}-RDS-Free-Storage --alarm-description "${rdsdbcluster} Free Storage" --metric-name FreeLocalStorage --namespace AWS/RDS --statistic Average --period 60 --threshold 10737418240 --comparison-operator LessThanThreshold  --dimensions "Name= DBClusterIdentifier,Value=${rdsdbcluster}" --evaluation-periods 3 --alarm-actions arn:aws:sns:us-east-1:490891715875:ADDA247-ORANGE-ALERT;
done < "./rdsdbcluster.txt"
